mapper.select:
select mem_email,mem_pass,mem_name,mem_num
from member
where mem_email=#{mem_email}

select count(mem_email)
from member
where mem_email=#{mem_email}

mapper.update:
UPDATE member
SET mem_pass = CASE WHEN #{mem_pass} IS NULL THEN mem_pass ELSE #{mem_pass} END ,
    mem_name=#{mem_name}, mem_nickname=#{mem_nickname}, mem_zipcode=#{mem_zipcode}
   , mem_phone=#{mem_phone}, mem_address1=#{mem_address1}, mem_address2=#{mem_address2},
    mem_edit_date=SYSDATE WHERE mem_email=#{mem_email}

mapper.select:
select count(*)
from member
where mem_email=#{mem_email} and mem_pass=#{mem_pass}

SELECT mem_pass
FROM member
WHERE mem_email = #{mem_email}

mapper.update:
UPDATE member
SET mem_pass = #{new_pass}
WHERE mem_email = #{mem_email}
