mapper.update:
update trip
set readcount=readcount + 1
where t_num = #{t_num}

mapper.select:
select t_num, t_subject, content, readcount, t_regdate
from trip
where t_num = #{t_num}

mapper.update:
update trip
set t_subject=#{t_subject}, content=#{content}
where t_num=#{t_num}

mapper.select:
SELECT * FROM ( SELECT rn, t_num, t_subject, content, readcount, t_regdate FROM ( SELECT ROW_NUMBER() OVER (ORDER BY t_num DESC) AS rn, t_num, t_subject, content, readcount, t_regdate FROM trip )
WHERE t_subject LIKE '%' || #{keyword} || '%'
WHERE content LIKE '%' || #{keyword} || '%'
WHERE t_subject LIKE '%' || #{keyword} || '%' or content LIKE '%' || #{keyword} || '%'
) WHERE rn between ${(pageNum -1) * pageRowCount} +1 and ${(pageNum -1) * pageRowCount} + ${pageRowCount}
