mapper.update:
update faq
set readcount=readcount + 1
where faq_num = #{faq_num}

mapper.select:
select faq_num, faq_subject, content, readcount, faq_reg_date
from faq
where faq_num = #{faq_num}

mapper.update:
update faq
set faq_subject=#{faq_subject},
    content=#{content}
where faq_num = #{faq_num}

mapper.select:
/* FaqMapper.listPage */ SELECT *
FROM ( SELECT rn, faq_num, faq_subject, content, readcount, faq_reg_date FROM ( SELECT ROW_NUMBER() OVER (ORDER BY faq_num DESC) AS rn, faq_num, faq_subject, content, readcount, faq_reg_date FROM faq
WHERE faq_subject LIKE '%' || #{keyword} || '%'
WHERE content LIKE '%' || #{keyword} || '%'
WHERE faq_subject LIKE '%' || #{keyword} || '%' or content LIKE '%' || #{keyword} || '%'
) ) WHERE rn between ${(pageNum -1) * pageRowCount} +1 and ${(pageNum -1) * pageRowCount} + ${pageRowCount}
